

import java.util.*;
public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int row = 1;
		while(row <= n)
		{
			int j = 1;
			while(j<= n)
			{
				System.out.print("* ");
				j++;
			}
			
			
			
			
			
			row++;
			System.out.println();
		}
		
	}

}
